sys_variable_value.value:
(function execute(inputs, outputs) {
var tableName = 'x_876909_meetnow_member';
var member_sys_id = 'e00fda5647d432105e50916a216d43d2'; // Shakeel Shaik Member Record

    var qrText = 'test'; // Your text to convert to QR code e.g. your URL etc

    var baseURL = "https://api.qrserver.com/v1/create-qr-code/";
    var qrData = "?data=" + qrText + "&size=100x100";

    var requestUrl = encodeURI(baseURL + qrData);
    var request = new sn_ws.RESTMessageV2();
    request.setHttpMethod('get');
    request.setEndpoint(requestUrl);
    request.setRequestHeader("Content-Type", "image/jpeg");
    request.saveResponseBodyAsAttachment(tableName, member_sys_id, qrText); // Save the body as attachment
    var pdfContentResponse = request.execute();
    outputs.pdf_content_response = pdfContentResponse;

    
})(inputs, outputs);

