sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    var tableName = 'x_876909_meetnow_member'; // 'x_876909_meetnow_member';
    gs.info('1 tableName = ' + tableName);
    var member_sys_id = current.sys_id; //'e00fda5647d432105e50916a216d43d2'; // Shakeel Shaik Member Record
    gs.info('2 member_sys_id = ' + member_sys_id);
    var qrText = current.mail_id; // 'test';  
    gs.info('3 qrText = ' + qrText);
    var baseURL = "https://api.qrserver.com/v1/create-qr-code/";
    var qrData = "?data=" + qrText + "&size=100x100";
    gs.info('4 qrData = ' + qrData);
    var requestUrl = encodeURI(baseURL + qrData);
    gs.info('5 requestUrl = ' + requestUrl);
    var request = new sn_ws.RESTMessageV2();
    request.setHttpMethod('get');
    request.setEndpoint(requestUrl);
    request.setRequestHeader("Content-Type", "image/jpeg");
    request.saveResponseBodyAsAttachment(tableName, member_sys_id, qrText); // Save the body as attachment
    var pdfContentResponse = request.execute();
    gs.info('6  pdfContentResponse = ' + pdfContentResponse);
})(current, previous);
